<?php

namespace AppBundle\Repository;

use AppBundle\Entity\Item;
use AppBundle\Entity\Match;
use AppBundle\Entity\User;
use Doctrine\ORM\EntityRepository;

/**
 * ItemRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ItemRepository extends EntityRepository
{
    /**
     * Returns array of items that matches these conditions:
     * 1. Item owner is in current user prefered locations list
     * 2. Item is available for trade (status = active)
     * 3. Item owner is not current user
     * 4. Item is in tradable item's categories list
     * 5. Item value matches tradable item's value with margin
     * 6. Item is not rejected by tradable item
     * 7. Item is not in the list of tradable item proposed match offers
     * 8. Item is not in the list of tradable item received match offers
     *
     * @param Item $item
     * @param User $user
     * @param int $margin
     * @param int $limit
     *
     * @return array
     */

    public function findAvailableMatches(Item $item, User $user, int $margin, int $limit): array
    {
        $subquery = $this->createQueryBuilder('s')
            ->select('s.id')
            ->leftJoin('s.matchesResponseItem', 'sm')
            ->where('sm.status = :status_accepted OR sm.status = :status_rejected')
            ->andWhere('sm.itemOwner = :id')
            ->setParameters([
                'status_accepted' => Match::STATUS_ACCEPTED,
                'status_rejected' => Match::STATUS_REJECTED,
                'id' => $item->getId(),
            ])
            ->getQuery()->getArrayResult();

        $exclude = array_map(function ($match) {
            return $match['id'];
        }, $subquery);

        $exclude[] = $item->getId();

        $query = $this
            ->createQueryBuilder('i')
            ->leftJoin('i.user', 'iu')
            ->andWhere('i.status = :status_active')
            ->andWhere('iu.id != :id')
            ->andWhere('i.category IN (:categories)')
            ->andWhere('i.value >= :min_value')
            ->andWhere('i.value <= :max_value')
            ->andWhere('i.id NOT IN (:exclude)')
            ->setParameters([
                'status_active' => Item::STATUS_ACTIVE,
                'id' => $user->getId(),
                'min_value' => $item->getValue() * (1 - $margin / 100),
                'max_value' => $item->getValue() * (1 + $margin / 100),
                'categories' => $item->getCategoriesToMatchArray(),
                'exclude' => $exclude,
            ])
            ->orderBy('RAND()')
            ->setMaxResults($limit);

        if ($user->getLocationsToMatchArray()) {
            $query
                ->andWhere('iu.location IN (:locations)')
                ->setParameter('locations', $user->getLocationsToMatchArray());
        }

        $items = $query
            ->getQuery()
            ->getResult();

        return $items;
    }

    /**
     * Returns array the most popular items
     *
     * @param int $limit
     *
     * @return array
     */
    public function findFeatured(int $limit): array
    {
        return $this
            ->createQueryBuilder('i')
            ->orderBy('i.approvals', 'DESC')
            ->setMaxResults($limit)
            ->getQuery()
            ->getResult();
    }

    /**
     * Return array of incoming item matches for given user
     *
     * @param User $user
     * @param int $status
     *
     * @return array
     */
    public function findItemsByMatchRespondent(User $user, int $status = Match::STATUS_ACCEPTED): array
    {
        $items = $this->createQueryBuilder('i')
            ->leftJoin('i.matchesResponseItem', 'm')
            ->where('i.user = :user')
            ->andWhere('m.status = :status')
            ->setParameters([
                'user' => $user,
                'status' => $status,
            ])
            ->getQuery()
            ->getResult();

        return $items;
    }

    /**
     * Return array of incoming item matches for given user
     *
     * @param User $user
     * @param int $status
     *
     * @return array
     */
    public function findItemsByMatchOwner(User $user, int $status = Match::STATUS_ACCEPTED): array
    {
        $items = $this->createQueryBuilder('i')
            ->leftJoin('i.matchesOwnItem', 'm')
            ->where('i.user = :user')
            ->andWhere('m.status = :status')
            ->setParameters([
                'user' => $user,
                'status' => $status,
            ])
            ->getQuery()
            ->getResult();

        return $items;
    }

    /**
     * Return array of item match respondents
     *
     * @param Item $item
     *
     * @return array
     */
    public function findItemMatchRespondents(Item $item): array
    {
        $items = $this->createQueryBuilder('i')
            ->leftJoin('i.matchesResponseItem', 'm')
            ->where('m.itemOwner = :item')
            ->setParameter('item', $item)
            ->getQuery()
            ->getResult();

        return $items;
    }

    /**
     * Return array of item match owners
     *
     * @param Item $item
     *
     * @return array
     */
    public function findItemMatchOwners(Item $item): array
    {
        $items = $this->createQueryBuilder('i')
            ->leftJoin('i.matchesOwnItem', 'm')
            ->where('m.itemRespondent = :item')
            ->andWhere('m.status = :status')
            ->setParameters([
                'item' => $item,
                'status' => Match::STATUS_ACCEPTED,
            ])
            ->getQuery()
            ->getResult();

        return $items;
    }
}
